import React, { useState } from 'react';
import ThemeToggle from './ThemeToggle';
import { useNavigate, Link } from 'react-router-dom';

const SignUp = ({ onSignUp }) => {
    const [mode, setMode] = useState(() => localStorage.getItem('themeMode') || 'light');
    React.useEffect(() => { localStorage.setItem('themeMode', mode); }, [mode]);
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('user');
    const [error, setError] = useState('');

    const handleSignUp = (e) => {
        e.preventDefault();
        setError('');

        if (username.length < 3 || password.length < 6) {
            setError('Username must be at least 3 characters and password at least 6 characters long.');
            return;
        }

        const newUser = { username, password, role };
        const success = onSignUp(newUser); // onSignUp now returns true/false
        if (success) {
            navigate('/');
        } else {
            setError('Username already exists. Please choose a different username.');
        }
    };

    const styles = {
        body: {
            fontFamily: 'Inter, sans-serif',
            backgroundImage: "url('https://www.ironfx.com/wp-content/uploads/2025/02/caterpillar-logo-building-stock.jpg')",
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            minHeight: '100vh',
            width: '100vw',
            height: '100vh',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            overflow: 'hidden',
            margin: 0,
            padding: 0,
            transition: 'background 0.3s',
            backgroundColor: mode === 'dark' ? '#18181b' : '#f3f4f6',
        },
        container: { maxWidth: '1024px', margin: 'auto' },
        h1: { fontSize: '2.25rem', fontWeight: 'bold', textAlign: 'center', marginBottom: '2rem', color: mode === 'dark' ? '#f3f4f6' : '#1a202c' },
        signUpContainer: {
            display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
            backgroundColor: mode === 'dark' ? 'rgba(34,34,34,0.95)' : 'rgba(255,255,255,0.9)',
            color: mode === 'dark' ? '#f3f4f6' : '#18181b',
            borderRadius: '1rem', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            padding: '2rem', marginBottom: '2rem'
        },
        h2: { fontSize: '1.5rem', fontWeight: '600', color: '#4a5568', marginBottom: '1rem' },
        form: { width: '100%', maxWidth: '24rem' },
        formGroup: { marginBottom: '1rem' },
        label: { display: 'block', color: '#4a5568', fontSize: '0.875rem', fontWeight: 'bold', marginBottom: '0.5rem' },
        input: {
            boxShadow: '0 1px 3px rgba(0,0,0,.12), 0 1px 2px rgba(0,0,0,.24)',
            appearance: 'none', border: '1px solid #e2e8f0', borderRadius: '0.25rem',
            width: '100%', padding: '0.5rem 0.75rem', color: '#4a5568',
            lineHeight: '1.25', outline: 'none',
        },
        button: {
            backgroundColor: '#38a169', color: 'white', fontWeight: 'bold', padding: '0.5rem 1rem',
            borderRadius: '0.25rem', outline: 'none',
        },
        link: {
            color: '#4299e1',
            textDecoration: 'none',
            fontSize: '0.875rem',
            marginTop: '1rem'
        },
        errorMessage: {
            color: '#e53e3e',
            fontSize: '0.875rem',
            marginTop: '0.5rem',
            textAlign: 'center'
        }
    };

    return (
        <div style={styles.body}>
            <ThemeToggle mode={mode} setMode={setMode} />
            <div style={styles.container}>
                <h1 style={{...styles.h1, textShadow: '2px 2px 4px rgba(0,0,0,0.5)'}}>Smart Rental Tracking Dashboard ðŸ“Š</h1>
                <div style={styles.signUpContainer}>
                    <h2 style={styles.h2}>Sign Up</h2>
                    <form onSubmit={handleSignUp} style={styles.form}>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="username">Username</label>
                            <input
                                id="username"
                                type="text"
                                required
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                style={styles.input}
                                placeholder="Choose a username"
                            />
                        </div>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="password">Password</label>
                            <input
                                id="password"
                                type="password"
                                required
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                style={styles.input}
                                placeholder="Choose a password"
                            />
                        </div>
                        <div style={styles.formGroup}>
                            <label style={styles.label} htmlFor="role">Role</label>
                            <select
                                id="role"
                                required
                                value={role}
                                onChange={(e) => setRole(e.target.value)}
                                style={styles.input}
                            >
                                <option value="user">User</option>
                                <option value="provider">Provider</option>
                            </select>
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexDirection: 'column' }}>
                            <button type="submit" style={styles.button}>Sign Up</button>
                            {error && <p style={styles.errorMessage}>{error}</p>}
                            <Link to="/" style={styles.link}>Already have an account? Log in</Link>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default SignUp;
